import React, { useState } from "react";
import Card from "../UI/Card";

import ExpenseItem from "./ExpenseItem";
import "./Expenses.css";
import ExpensesFilter from "./ExpensesFilter";
function Expenses(props) {
  const [filteredYear, setFilteredYear] = useState("2019");
  const filterHandler = (date) => {
    setFilteredYear(date);
    console.log(date);
  };
  const filteredYearsToShow = () => {
    props.expenses.filter((item) => {
      return item.date.getFullYear().toString() === filteredYear;
    });
  };
  return (
    <Card className="expenses">
      <ExpensesFilter selectedYear={filteredYear} onFilter={filterHandler} />
      {props.expenses.map((curr) => {
        return <ExpenseItem item={curr} key={curr.id} />;
      })}
    </Card>
  );
}

export default Expenses;
