import React, { useState } from "react";
import Card from "../UI/Card";

import ExpenseItem from "./ExpenseItem";
import "./Expenses.css";
import ExpensesFilter from "./ExpensesFilter";
function Expenses(props) {
  const [filteredYear, setFilteredYear] = useState("2019");

  const filterHandler = (date) => {
    setFilteredYear(date);
  };
  const filteredYearsToShow = props.expenses.filter((item) => {
    console.log(filteredYear + " ho");
    return item.date.getFullYear().toString() === filteredYear;
  });

  return (
    <Card className="expenses">
      <ExpensesFilter selectedYear={filteredYear} onFilter={filterHandler} />
      {filteredYearsToShow.length > 0 &&
        filteredYearsToShow.map((curr) => {
          return <ExpenseItem item={curr} key={curr.id} />;
        })}
      {/* //adding conditional content. */}
      {filteredYearsToShow.length === 0 && <h2>No Expenses Found.</h2>}
    </Card>
  );
}

export default Expenses;
