import React from "react";
import Card from "../UI/Card";

import ExpenseItem from "./ExpenseItem";
import "./Expenses.css";
import ExpensesFilter from "./ExpensesFilter";
function Expenses(props) {
  const [filteredYear, setFilteredYear] = useState("2019");

  const filterHandler = (date) => {
    setFilteredYear(date);
  };
  const filteredYearsToShow = props.expenses.filter((item) => {
    console.log(filteredYear + " ho");
    return item.date.getFullYear().toString() === filteredYear;
  });
  let expensesContent = <h2>No Expenses Found.</h2>;
  if (filteredYearsToShow.length > 0) {
    expensesContent = filteredYearsToShow.map((curr) => {
      return <ExpenseItem item={curr}></ExpenseItem>;
    });
  }

  return (
    <Card className="expenses">
      <ExpensesFilter selectedYear={filteredYear} onFilter={filterHandler} />
      {expensesContent}
    </Card>
  );
}

export default Expenses;
